#pragma once

#include <memory>
#include "command_base.h"



namespace Sdx
{
  namespace Cmd
  {
    ///
    /// Enable (or disable) the use of a manually imported SBAS message sequence instead of the automatically generated one.
    ///
    /// Name    Type Description
    /// ------- ---- ----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
    /// Enabled bool If enabled (true), Skydel will use a manually imported SBAS message sequence as the source. If disabled (false, default), the SBAS message sequence will be automatically generated by Skydel.
    ///

    class EnableManualSbasMessageSequence;
    typedef std::shared_ptr<EnableManualSbasMessageSequence> EnableManualSbasMessageSequencePtr;
    
    
    class EnableManualSbasMessageSequence : public CommandBase
    {
    public:
      static const char* const CmdName;
      static const char* const Documentation;
      static const char* const TargetId;


      EnableManualSbasMessageSequence();

      EnableManualSbasMessageSequence(bool enabled);

      static EnableManualSbasMessageSequencePtr create(bool enabled);
      static EnableManualSbasMessageSequencePtr dynamicCast(CommandBasePtr ptr);
      virtual bool isValid() const override;
      virtual std::string documentation() const override;
      virtual const std::vector<std::string>& fieldNames() const override;

      virtual int executePermission() const override;


      // **** enabled ****
      bool enabled() const;
      void setEnabled(bool enabled);
    };
    
  }
}

